mapper.select:
SELECT t.id, t.teacher_no, t.user_id, u.username, u.name, t.department_id, d.name AS department_name, t.title, t.gender, CASE t.gender WHEN 0 THEN '男' WHEN 1 THEN '女' END AS gender_desc, t.id_card, t.birthday, t.entry_date, t.status, CASE t.status WHEN 0 THEN '在职' WHEN 1 THEN '离职' WHEN 2 THEN '退休' END AS status_desc, u.email, u.phone, u.avatar, t.create_time, t.update_time FROM teacher t JOIN sys_user u ON t.user_id = u.id JOIN department d ON t.department_id = d.id
AND t.teacher_no LIKE CONCAT('%', #{teacherNo}, '%')
AND u.name LIKE CONCAT('%', #{name}, '%')
AND t.department_id = #{departmentId}
AND t.title = #{title}
AND t.status = #{status}
AND u.email LIKE CONCAT('%', #{email}, '%')
AND u.phone LIKE CONCAT('%', #{phone}, '%')
AND t.birthday >= #{birthdayStart}
AND t.birthday <= #{birthdayEnd}
AND t.entry_date >= #{entryDateStart}
AND t.entry_date <= #{entryDateEnd}
ORDER BY t.create_time DESC

SELECT co.id, c.id AS course_id, c.name AS course_name, c.code AS course_code, c.credit, c.hours, c.type AS course_type, CASE c.type WHEN 0 THEN '必修' WHEN 1 THEN '选修' END AS course_type_desc, co.semester, co.class_time, co.location, co.capacity, co.selected_count, co.status, CASE co.status WHEN 0 THEN '正常' WHEN 1 THEN '已结课' END AS status_desc, co.create_time, co.update_time FROM course_offering co JOIN course c ON co.course_id = c.id WHERE co.teacher_id = #{teacherId}
AND co.semester = #{semester}
AND c.name LIKE CONCAT('%', #{courseName}, '%')
AND co.status = #{status}
ORDER BY co.create_time DESC

SELECT cs.id, s.id AS student_id, s.student_no, u.name AS student_name, s.class_id, cl.name AS class_name, m.name AS major_name, d.name AS department_name, s.gender, CASE s.gender WHEN 0 THEN '男' WHEN 1 THEN '女' END AS gender_desc, cs.selection_time, cs.score, cs.status, CASE cs.status WHEN 0 THEN '已选' WHEN 1 THEN '已退' WHEN 2 THEN '已修完' END AS status_desc, ( SELECT COUNT(CASE WHEN a.status = 0 THEN 1 END) * 100.0 / COUNT(*) FROM attendance a WHERE a.student_id = s.id AND a.course_offering_id = #{courseId} ) AS attendance_rate, ( SELECT a.status FROM attendance a WHERE a.student_id = s.id AND a.course_offering_id = #{courseId} ORDER BY a.attendance_date DESC LIMIT 1 ) AS last_attendance_status, ( SELECT a.attendance_date FROM attendance a WHERE a.student_id = s.id AND a.course_offering_id = #{courseId} ORDER BY a.attendance_date DESC LIMIT 1 ) AS last_attendance_date, ( SELECT CASE a.status WHEN 0 THEN '出勤' WHEN 1 THEN '缺勤' WHEN 2 THEN '迟到' WHEN 3 THEN '早退' WHEN 4 THEN '请假' END FROM attendance a WHERE a.student_id = s.id AND a.course_offering_id = #{courseId} ORDER BY a.attendance_date DESC LIMIT 1 ) AS last_attendance_desc FROM course_selection cs JOIN student s ON cs.student_id = s.id JOIN sys_user u ON s.user_id = u.id JOIN class cl ON s.class_id = cl.id JOIN major m ON cl.major_id = m.id JOIN department d ON m.department_id = d.id WHERE cs.course_offering_id = #{courseId}
AND u.name LIKE CONCAT('%', #{studentName}, '%')
AND s.student_no LIKE CONCAT('%', #{studentNo}, '%')
AND cs.status = #{status}
ORDER BY cs.selection_time DESC
